#define VERSION 215#include "jttoolkit.h"#include "Global.h"using namespace std;Global *instance;namespace jttoolkit{  void setup(){    glutSwapBuffers();//a little trick to make the window go black while it waits.    instance = new Global();      }    void loop(){    instance->loop();  }    void jttoolkit_keyDown(int k,int special){    if(k==27){      exit(0);    }    instance->keyDown(k,special);  }    void jttoolkit_keyUp(int k,int special){}    void jttoolkit_mouseDown(){    instance->mouseDown();      }    void jttoolkit_mouseUp(){    instance->mouseUp();  }    void stop(){  }    void ignored_mouseMove(int x,int y){  }      void ignored_mouseDown(int x,int y){      }    void ignored_mouseUp(int x,int y){  }  void jttoolkit_windowReshaped(int winX,int winY,int w,int h){    instance->windowReshaped(winX,winY,w,h);  }};